@app.route('/add_student', methods=['POST'])
def add_student():
    """Add new student - handles both JSON and form data"""
    try:
        print("ğŸ” DEBUG: Add student POST request received")
        
        # Handle both JSON and form data
        if request.is_json:
            # JSON request from JavaScript
            data = request.get_json()
            name = data.get('name')
            student_id = data.get('student_id')
            email = data.get('email', '')
            print(f"ğŸ” DEBUG: JSON data - Name: {name}, ID: {student_id}, Email: {email}")
        else:
            # Form data request (traditional form submission)
            name = request.form.get('name')
            student_id = request.form.get('student_id')
            email = request.form.get('email', '')
            print(f"ğŸ” DEBUG: Form data - Name: {name}, ID: {student_id}, Email: {email}")
        
        # Validate required fields
        if not name or not student_id:
            error_msg = 'Name and Student ID are required'
            print(f"âŒ DEBUG: Validation failed - {error_msg}")
            if request.is_json:
                return jsonify({'success': False, 'message': error_msg})
            else:
                flash(error_msg, 'error')
                return redirect(url_for('add_student_page'))
        
        # Clean and validate input
        name = name.strip()
        student_id = student_id.strip().upper()
        email = email.strip() if email else ''
        
        print(f"ğŸ” DEBUG: Cleaned data - Name: '{name}', ID: '{student_id}', Email: '{email}'")
        
        # Validate student ID format (optional - customize as needed)
        if len(student_id) < 3:
            message = 'Student ID must be at least 3 characters long'
            print(f"âŒ DEBUG: ID validation failed - {message}")
            if request.is_json:
                return jsonify({'success': False, 'message': message})
            else:
                flash(message, 'error')
                return redirect(url_for('add_student_page'))
        
        # Add student to database
        print(f"ğŸ” DEBUG: Attempting to add student to database...")
        success = db.add_student(name, student_id, email)
        print(f"ğŸ” DEBUG: Database add_student result: {success}")
        
        if success:
            message = f'Student {name} (ID: {student_id}) added successfully!'
            print(f"âœ… DEBUG: {message}")
            
            # Verify student was added
            updated_students = db.get_students()
            print(f"ğŸ” DEBUG: Database now has {len(updated_students)} students")
            
            if request.is_json:
                return jsonify({'success': True, 'message': message})
            else:
                flash(message, 'success')
                return redirect(url_for('index'))
        else:
            message = f'Student ID {student_id} already exists. Please use a different ID.'
            print(f"âŒ DEBUG: {message}")
            if request.is_json:
                return jsonify({'success': False, 'message': message})
            else:
                flash(message, 'error')
                return redirect(url_for('add_student_page'))
            
    except Exception as e:
        error_message = f'Error adding student: {str(e)}'
        print(f"âŒ DEBUG: Exception in add_student: {error_message}")
        import traceback
        traceback.print_exc()
        
        if request.is_json:
            return jsonify({'success': False, 'message': error_message})
        else:
            flash(error_message, 'error')
            return redirect(url_for('add_student_page'))

@app.route('/warm_camera', methods=['POST'])
def warm_camera():
    """Pre-warm camera for faster detection"""
    camera = None
    try:
        print("ğŸ”¥ Pre-warming camera...")
        camera = cv2.VideoCapture(0)
        
        if not camera.isOpened():
            return jsonify({'success': False, 'message': 'Camera not available'})
        
        # Quick optimized settings
        camera.set(cv2.CAP_PROP_BUFFERSIZE, 1)
        camera.set(cv2.CAP_PROP_FRAME_WIDTH, 320)
        camera.set(cv2.CAP_PROP_FRAME_HEIGHT, 240)
        camera.set(cv2.CAP_PROP_FPS, 15)
        
        # Read a few frames to warm up
        frames_read = 0
        for i in range(5):
            ret, frame = camera.read()
            if ret:
                frames_read += 1
                print(f"ğŸ”¥ Warm-up frame {i+1}/5 - Success")
            else:
                print(f"ğŸ”¥ Warm-up frame {i+1}/5 - Failed")
        
        camera.release()
        camera = None
        
        return jsonify({
            'success': True, 
            'message': f'ğŸ”¥ Camera warmed up! Read {frames_read}/5 frames. Detection will be faster now.',
            'frames_read': frames_read
        })
        
    except Exception as e:
        print(f"âŒ Camera warm-up error: {e}")
        return jsonify({'success': False, 'message': f'Warm-up error: {str(e)}'})
    finally:
        if camera is not None:
            try:
                camera.release()
            except:
                pass

@app.route('/start_detection', methods=['POST'])
def start_detection():
    """Start anti-spoofing detection for selected student - OPTIMIZED VERSION"""
    camera = None
    try:
        print("ğŸ” DEBUG: start_detection route called")
        
        # Handle both JSON and form data
        if request.is_json:
            data = request.get_json()
            student_id = data.get('student_id')
            student_name = data.get('student_name')
            print(f"ğŸ” DEBUG: JSON data - ID: {student_id}, Name: {student_name}")
        else:
            student_id = request.form.get('student_id')
            student_name = request.form.get('student_name')
            print(f"ğŸ” DEBUG: Form data - ID: {student_id}, Name: {student_name}")
        
        # Validate student_id is provided
        if not student_id:
            return jsonify({'success': False, 'message': 'Please select a student from the dropdown'})
        
        # Get student info from database if name not provided
        if not student_name:
            print(f"ğŸ” DEBUG: Looking up student name...")
            student = db.get_student_by_id(student_id)
            if student:
                student_name = student['name']
                print(f"ğŸ” DEBUG: Found student name: {student_name}")
            else:
                return jsonify({'success': False, 'message': f'Student with ID {student_id} not found'})
        
        print(f"ğŸ” DEBUG: Starting FAST detection for {student_name} ({student_id})")
        
        # âš¡ OPTIMIZED CAMERA INITIALIZATION
        print("ğŸ“· Initializing camera with optimized settings...")
        camera = cv2.VideoCapture(0)
        
        # Set buffer size to 1 for faster processing
        camera.set(cv2.CAP_PROP_BUFFERSIZE, 1)
        
        # Lower resolution for faster processing
        camera.set(cv2.CAP_PROP_FRAME_WIDTH, 320)   # Reduced from 640
        camera.set(cv2.CAP_PROP_FRAME_HEIGHT, 240)  # Reduced from 480
        camera.set(cv2.CAP_PROP_FPS, 15)            # Reduced from 30
        
        # Additional optimizations
        try:
            camera.set(cv2.CAP_PROP_FOURCC, cv2.VideoWriter_fourcc('M', 'J', 'P', 'G'))
        except:
            pass  # Some cameras don't support this
        
        if not camera.isOpened():
            return jsonify({'success': False, 'message': 'Camera not available. Please check connection.'})
        
        # âš¡ WARM UP CAMERA (read a few frames to stabilize)
        print("ğŸ”¥ Warming up camera...")
        for i in range(3):
            ret, frame = camera.read()
            if not ret:
                print(f"âš ï¸ Warm-up frame {i+1} failed")
                break
            else:
                print(f"âœ… Warm-up frame {i+1} success")
        
        # Initialize detector
        detector = AntiSpoofDetector()
        
        # âš¡ FASTER DETECTION PROCESS
        start_time = time.time()
        detection_duration = 3  # Reduced from 5 seconds
        frames_processed = 0
        face_detected_count = 0
        max_frames = 30  # Limit total frames processed
        
        print(f"ğŸ” DEBUG: Starting {detection_duration}s FAST detection...")
        
        while (time.time() - start_time < detection_duration) and (frames_processed < max_frames):
            ret, frame = camera.read()
            if not ret:
                print("âŒ DEBUG: Failed to read frame")
                break
            
            # âš¡ SKIP FRAMES FOR FASTER PROCESSING
            if frames_processed % 2 == 0:  # Process every 2nd frame only
                result = detector.analyze_frame(frame)
                if result['face_detected']:
                    face_detected_count += 1
                    print(f"ğŸ‘¤ Face detected in frame {frames_processed}")
            
            frames_processed += 1
            
            # Smaller delay for faster processing
            time.sleep(0.05)  # Reduced from 0.1
        
        # Clean up camera immediately
        camera.release()
        camera = None
        
        print(f"ğŸ” DEBUG: FAST detection completed - {frames_processed} frames, {face_detected_count} with faces")
        
        # Determine result based on detection
        if face_detected_count == 0:
            status = "NO_FACE_DETECTED"
            details = "No face detected. Please ensure you're visible to the camera."
        elif detector.is_live_person():
            status = "PRESENT"
            details = f"Live person detected - Blinks: {detector.total_blinks}, Motion: {'Yes' if detector.motion_detected else 'No'}"
        else:
            status = "SPOOFING_DETECTED"
            details = f"Possible spoofing - Blinks: {detector.total_blinks}, Motion: {'Yes' if detector.motion_detected else 'No'}"
        
        print(f"ğŸ” DEBUG: Result - Status: {status}")
        
        # Save to database
        print(f"ğŸ” DEBUG: Saving to database...")
        db.mark_attendance(
            student_name=student_name,
            student_id=student_id,
            status=status,
            blinks=detector.total_blinks,
            motion_detected=detector.motion_detected,
            details=details
        )
        print(f"âœ… DEBUG: Attendance saved for {student_name}")
        
        return jsonify({
            'success': True,
            'status': status,
            'blinks': detector.total_blinks,
            'motion': detector.motion_detected,
            'details': details,
            'frames_processed': frames_processed,
            'face_detection_rate': f"{(face_detected_count/frames_processed)*100:.1f}%" if frames_processed > 0 else "0%",
            'message': f'âœ… Fast detection completed for {student_name}',
            'processing_time': f"{time.time() - start_time:.1f}s"
        })
        
    except Exception as e:
        print(f"âŒ DEBUG: Exception in start_detection: {str(e)}")
        import traceback
        traceback.print_exc()
        return jsonify({'success': False, 'message': f'Detection error: {str(e)}'})
    
    finally:
        # Ensure camera is always released
        if camera is not None:
            try:
                camera.release()
                print("ğŸ“· Camera released")
            except:
                pass

